/** Input type NUMBER  */
/** Use:
    <div class="field">
    <input type="number" min="1" max="10" step="1" value="5">
    </div>
*/
input[type=number]::-webkit-inner-spin-button,
input[type=number]::-webkit-outer-spin-button {
  -webkit-appearance: none;
  margin: 0;
}
input[type=number] {
  -moz-appearance: textfield;
  appearance: textfield;
}
.field input {
  outline: 0; 
  border: none;
  width: 42px;
  height: 32px;
  line-height: 1.65;
  padding: 0;
  margin: 0;
  padding-left: 0px;
  border: 1px solid red;
}
.field input:focus {
  outline: 0;
}
.field-nav {
  height: 32px;
}
.field-button {
  position: relative;
  cursor: default;
  border-left: 1px solid #ccc;
  width: 14px;
  text-align: center;
  color: #333;
  font-size: 13px;
  font-family: "Trebuchet MS", Helvetica, sans-serif !important;
  line-height: 1.7;
  -webkit-transform: translateX(-100%);
  transform: translateX(-100%);
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  -o-user-select: none;
  user-select: none;
}
.field-button.field-up {
  position: absolute;
  height: 50%;
  top: 0;
  border-bottom: 1px solid #eee;
}
.field-button.field-down {
  position: absolute;
  bottom: -1px;
  height: 50%;
}

/* ///// Range Slider ////  */
.slider {
  margin: 0 0 0 0;
}
input[type=range] {
  height: 12px;
  -webkit-appearance: none;
  appearance: none;
  margin: 5px 0 3px 0;
  width: 100%;
}
input[type=range]:focus {
  outline: none;
}
input[type=range]:focus::-webkit-slider-runnable-track {
  background: #31669c;
}
input[type=range]::-webkit-slider-runnable-track {
  width: 100%;
  height: 5px;
  cursor: pointer;
  transition: 0.2s;
  box-shadow: 1px 1px 3px #12505cbd;
  background: #31669c;
  border-radius: 7px;
  border: 1px solid #010101;
}
input[type=range]::-moz-range-track {
  width: 100%;
  height: 5px;
  cursor: pointer;
  transition: 0.2s;
  box-shadow: 1px 1px 3px #12505Cbd;
  background: #31669c;
  border-radius: 7px;
  border: 1px solid #010101;
}
/*
input[type="range"]::-moz-range-progress {
  background: #5697D8;
}
*/
input[type=range]::-ms-track {
  width: 100%;
  height: 5px;
  cursor: pointer;
  transition: 0.2s;
  background: transparent;
  border-color: transparent;
  color: transparent;
}
input[type=range]::-webkit-slider-thumb {
  box-shadow: 1px 1px 4px #555C70bd;
  border: 2px solid #3B6987;
  height: 12px;
  width: 12px;
  border-radius: 5px;
  background: #F2F2F2;
  cursor: pointer;
  -webkit-appearance: none;
  margin-top: -4.5px;
}
input[type=range]::-moz-range-thumb {
  box-shadow: 1px 1px 4px #555C70bd;
  border: 2px solid #3B6987;
  height: 12px;
  width: 12px;
  border-radius: 5px;
  background: #F2F2F2;
  cursor: pointer;
}
input[type=range]::-ms-thumb {
  margin-top: 1px;
  box-shadow: 1px 1px 4px #555C70bd;
  border: 2px solid #3B6987;
  height: 12px;
  width: 12px;
  border-radius: 5px;
  background: #F2F2F2;
  cursor: pointer;
}
input[type=range]::-ms-fill-lower {
  background: #31669c;
  border: 1px solid #010101;
  border-radius: 12px;
  box-shadow: 1px 1px 3px #12505Cbd;
}
input[type=range]::-ms-fill-upper {
  background: #31669c;
  border: 1px solid #010101;
  border-radius: 12px;
  box-shadow: 1px 1px 3px #12505Cbd;
}
input[type=range]:focus::-ms-fill-lower {
  background: #31669c;
}
input[type=range]:focus::-ms-fill-upper {
  background: #31669c;
}

/* to make a slider appear disabled, add class 'disabled' */
.disabled {
    background: transparent!important;
    pointer-events: none!important;
    opacity:0.4;
    cursor: none;
}
.disabled input[type=range]:focus::-webkit-slider-runnable-track {
    background: transparent!important;
    pointer-events: none!important;
    opacity:0.4;
  }
.disabled input[type=range]::-webkit-slider-runnable-track {
    width: 100%;
    height: 5px;
    cursor: none;
    box-shadow: 1px 1px 3px transparent!important;
    background:  transparent!important;
    border-radius: 7px;
    border: 1px solid transparent!important;
    pointer-events: none;
    opacity:0.4;
  }
.disabled input[type=range]::-moz-range-track {
    width: 100%;
    height: 5px;
    cursor: none;
    box-shadow: 1px 1px 3px transparent!important;
    background: transparent!important;
    border-radius: 7px;
    border: none!important;
    pointer-events: none;
    opacity:0.4;
  }
